import React from 'react';
import './Emotions.css';

class Emotions extends React.Component {
    constructor(props) {
        super(props);
        this.handleEmotionChange = this.handleEmotionChange.bind(this);
    }

    handleEmotionChange(e) {
        this.props.onEmotionChange(e.target.value);
    }

    render() {
        return (
        <div id="emotions" className="container">
        <div className="row emotion-row">
            <div className="col-3">
              <button type="button" className="btn btn-outline-light" value="angry" onClick={this.handleEmotionChange}>angry</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">happy</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">sad</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">frustrated</button>
            </div>
        </div>
        <div className="row emotion-row">
            <div className="col-3">
            <button type="button" className="btn btn-outline-light">anxious</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">emotionless</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">demotivated</button>
            </div>
            <div className="col-3">
               <button type="button" className="btn btn-outline-light">stressed</button>
            </div>
         </div>
        </div>
        )
    }
}

export default Emotions;
