import React from 'react';
import './App.css';
import Header from './Components/Header/Header';
import Emotions from './Components/Emotions/Emotions';
import Advice from './Components/Advice/Advice';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      emotion: ''
    };

    this.changeEmotion = this.changeEmotion.bind(this);
  }

  changeEmotion(emotion) {
    this.setState({ emotion: emotion });
  }

  render() {
    return (
      <div className="App">
        {this.state.emotion ? null: <Header />}

        <Emotions onEmotionChange={this.changeEmotion}/>

        {this.state.emotion ? <Advice emotion={this.state.emotion}/> : null}

        <footer>
          &copy; 2020 Franziska Nelson
        </footer>
      </div>
    );
  }
}

export default App;
